#include <stdint.h>
#include <stdio.h>
#include<led.h>
#include<FreeRTOS.h>
#include<FreeRTOSConfig.h>
#include<task.h>

#if !defined(__SOFT_FP__) && defined(__ARM_FP)
  #warning "FPU is not initialized, but the project is compiling for an FPU. Please initialize the FPU before use."
#endif

void vLedTask1(void *pvParam)
{
	portTickType pxPreviousWakeupTime = xTaskGetTickCount();
	while(1)
	{
		led_toggle(LED_BLUE);
		vTaskDelayUntil(&pxPreviousWakeupTime, 1000 / portTICK_RATE_MS);
	}

	vTaskDelete(NULL);
}

void vLedTask2(void *pvParam)
{
	portTickType pxPreviousWakeupTime = xTaskGetTickCount();
	while(1)
	{
		led_toggle(LED_GREEN);
		vTaskDelayUntil(&pxPreviousWakeupTime, 1000 / portTICK_RATE_MS);
	}

	vTaskDelete(NULL);
}

