int main(void)
{
	led_init(LED_BLUE);
	led_init(LED_GREEN);
	led_init(LED_ORANGE);
	led_init(LED_RED);

	xTaskCreate(vLedTask1, "LedTask1", configMINIMAL_STACK_SIZE, NULL, 4, NULL);
	xTaskCreate(vLedTask2, "LedTask2", configMINIMAL_STACK_SIZE, NULL, 4, NULL);
	xTaskCreate(vLedTask3, "LedTask3", configMINIMAL_STACK_SIZE, NULL, 4, NULL);
	xTaskCreate(vLedTask4, "LedTask4", configMINIMAL_STACK_SIZE, NULL, 4, NULL);

	vTaskStartScheduler();

	while(1);
}

